import React, { useState } from "react";
import styled from "styled-components";
import { Sudoku } from "./Sudoku.js";
import { colors } from "../colors.js";

export const SudokuControls = (props) => {
  const [state, setState] = useState([]);

  var localState = {
    sudoku: [],
    houses: [],
    rows: [],
    cols: [],
    unsolved: [],
  };

  var input = [
    [0, 4, 9, 0, 0, 0, 0, 3, 0],
    [0, 5, 0, 6, 1, 0, 0, 0, 0],
    [0, 0, 8, 0, 2, 9, 5, 0, 6],
    [8, 0, 0, 9, 0, 7, 0, 0, 4],
    [7, 0, 0, 0, 0, 0, 0, 8, 1],
    [0, 2, 5, 0, 4, 1, 3, 0, 0],
    [2, 0, 0, 0, 7, 6, 0, 1, 0],
    [5, 0, 0, 4, 0, 8, 7, 0, 0],
    [0, 8, 7, 0, 0, 0, 0, 9, 5],
  ];

  //useEffect(() => {
  const initSudoku = () => {
    loadSudoku(input);

    while (state.unsolved.length > 0 && getNextSolution());
  };
  //}, []);

  // input is a 2d array of all initial values
  const loadSudoku = (input) => {
    console.log("***SUDOKU LOADING***");

    let sudoku = buildEmpty2DArray();
    let houses = buildEmpty2DArray();
    let rows = buildEmpty2DArray();
    let cols = buildEmpty2DArray();
    let unsolved = [];

    for (let y = 0; y < 9; y++) {
      for (let x = 0; x < 9; x++) {
        // create cell object
        let val = input[y][x];
        let isSet = val >= 1 && val <= 9;
        var cell = buildNewCell(val, isSet);

        // store cell in each state object
        let h = 3 * Math.floor(y / 3) + Math.floor(x / 3);
        let r = 3 * (y % 3) + (x % 3);

        sudoku[h][r] = cell;
        houses[h][r] = val;
        rows[y][x] = val;
        cols[x][y] = val;

        if (!isSet) {
          var s = new Set();
          for (let i = 1; i <= 9; i++) s.add(i);

          unsolved.push({
            row: y,
            col: x,
            house: h,
            room: r,
            suspects: s,
          });
        }
      }
    }

    // identify each cell's suspect values
    let axes = [rows, cols, houses];
    for (let i = 0; i < unsolved.length; i++) {
      findSuspects(unsolved[i], axes);
    }

    // push to Sudoku as props
    localState = {
      sudoku: sudoku,
      houses: houses,
      rows: rows,
      cols: cols,
      unsolved: unsolved,
    };
    setState(localState);

    // TODO: get notes to properly post to state
    console.log(sudoku);

    console.log("***SUDOKU LOADED***");
  };

  // TODO: replace useEffect w this once tested
  const getNextSolution = () => {
    // Naked Single check
    for (let i = 0; i < state.unsolved.length && i < 81; i++) {
      if (state.unsolved[i].suspects.size === 1) {
        // remove cell from unsolved list
        var cell = state.unsolved.splice(i, 1)[0];
        let suspect = cell.suspects.values().next().value;

        // report to console
        console.log(
          "Naked Single @ (" + cell.row + "," + cell.col + ") = " + suspect
        );

        // update state w suspect data
        state.sudoku[cell.house][cell.room].val = suspect;
        state.houses[cell.house][cell.room] = suspect;
        state.rows[cell.row][cell.col] = suspect;
        state.cols[cell.col][cell.row] = suspect;
        for (let i = 0; i < state.unsolved.length; i++) {
          let cell2 = state.unsolved[i];
          if (
            cell2.row === cell.row ||
            cell2.col === cell.col ||
            cell2.house === cell.house
          ) {
            findSuspects(state.unsolved[i]);
          }
        }

        // push state as props to Sudoku
        localState = {
          sudoku: state.sudoku,
          houses: state.houses,
          rows: state.rows,
          cols: state.cols,
          unsolved: state.unsolved,
        };
        setState(localState);
        return true;
      }
    }

    // Hidden Single check
    // ...

    console.log("No Solution Found.");
    return false;
  };

  const findSuspects = (
    cell,
    axes = [state.rows, state.cols, state.houses]
  ) => {
    let indexes = [cell.row, cell.col, cell.house];

    for (let i = 0; i < 3; i++) {
      // loops through rows, cols, houses this cell belongs to
      axes[i][indexes[i]].forEach((val) => {
        // if the value is set, remove it from suspect list
        if (val >= 1 && val <= 9) {
          cell.suspects.delete(val);
        }
      });
    }
  };

  const buildEmpty2DArray = () => {
    var result = new Array(9);
    for (let i = 0; i < 9; i++) {
      result[i] = new Array(9);
    }
    return result;
  };

  const buildNewCell = (v, s) => {
    return {
      val: v,
      set: s,
      notes: [false, false, false, false, false, false, false, false, false],
    };
  };

  return (
    <StyledDiv>
      <Sudoku houses={state.sudoku}></Sudoku>
      <Controls>
        <Button onClick={() => loadSudoku(input)}>load</Button>
        <Button onClick={getNextSolution}>solve</Button>
      </Controls>
    </StyledDiv>
  );
};

const StyledDiv = styled.div`
  display: grid;
  grid-template-rows: min(80vw, 80vh) 1fr;
  grid-template-columns: 1fr;
  grid-gap: 2rem;
  width: min(80vw, 80vh);
  height: 100%;
`;

const Controls = styled.div`
  display: flex;
  flex-direction: row;
  gap: 2em;
  width: 100%;
`;

const Button = styled.button`
  flex-grow: 1;
  font-size: 24px;

  border: 2px solid ${colors.sudokuBorder};
  color: ${colors.sudokuBorder};
  background-color: transparent;
  padding: 0.2em;
  border-radius: 1em;
  transition: all 0.3s ease;
  &:hover {
    background-color: ${colors.sudokuBorder};
    color: ${colors.appBG};
    transform: scale(1.05);
  }
  &:active {
    transition: all 0.1 ease;
    background-color: ${colors.sudokuBG1};
    transform: scale(1.025);
    //color: ${colors.sudokuBorder};
  }
`;
