  check: (key, cell, state, showcase) => {
    // if this cell has only one suspect...
    if (cell.suspects.size === 1) {
      // ...report that suspect's value.

      // TODO: replace this...

      // ...with this
      // 1) create a copy of state.sudoku
      // 2) update this cell in the copy cell w new value
      let solution = cell.suspects.values().next().value;
      state.sudoku.rows[cell.row][cell.col].val = solution;
      showcase.rows[cell.row][cell.col].notes[solution - 1] = 1;

      console.log(
        this.name + "\t@ (" + cell.row + ", " + cell.col + ") = " + solution
      );

      state.unsolved.delete(key);
      // 3) return the new sudoku
      return showcase;
    }
    return false;
  },
};
