import React, { useState } from "react";
import styled from "styled-components";
import { Sudoku } from "./Sudoku.js";
import { colors } from "../colors.js";
import { nakedSingle } from "./NakedSingle.js";
import { hiddenSingle } from "./HiddenSingle.js";

export const SudokuControls = (props) => {
  const [state, setState] = useState([]);

  var localState = {
    sudoku: [],
    houses: [],
    rows: [],
    cols: [],
    unsolved: [],
  };

  var techniques = [hiddenSingle, nakedSingle];

  // Naked Single Test
  // var input = [
  //   [0, 4, 9, 0, 0, 0, 0, 3, 0],
  //   [0, 5, 0, 6, 1, 0, 0, 0, 0],
  //   [0, 0, 8, 0, 2, 9, 5, 0, 6],
  //   [8, 0, 0, 9, 0, 7, 0, 0, 4],
  //   [7, 0, 0, 0, 0, 0, 0, 8, 1],
  //   [0, 2, 5, 0, 4, 1, 3, 0, 0],
  //   [2, 0, 0, 0, 7, 6, 0, 1, 0],
  //   [5, 0, 0, 4, 0, 8, 7, 0, 0],
  //   [0, 8, 7, 0, 0, 0, 0, 9, 5],
  // ];

  // Hidden Single Test
  var input = [
    [0, 7, 2, 3, 0, 0, 9, 0, 0],
    [0, 5, 0, 6, 0, 9, 0, 2, 0],
    [6, 0, 0, 0, 1, 0, 0, 3, 0],
    [0, 0, 0, 0, 0, 0, 2, 0, 0],
    [0, 4, 5, 8, 2, 3, 0, 7, 0],
    [0, 3, 0, 4, 0, 0, 0, 0, 8],
    [7, 0, 0, 0, 0, 0, 0, 0, 4],
    [0, 2, 0, 5, 0, 8, 0, 6, 0],
    [0, 0, 3, 0, 0, 1, 7, 0, 0],
  ];

  // input is a 2d array of all initial values
  const loadSudoku = (input) => {
    console.log("Loading Sudoku...");

    let sudoku = buildEmpty2DArray();
    let houses = buildEmpty2DArray();
    let rows = buildEmpty2DArray();
    let cols = buildEmpty2DArray();
    let unsolved = [];

    // consider switching x/y parsing w h/r parsing as it
    // would add a more authentic flare to the search order
    for (let y = 8; y >= 0; y--) {
      for (let x = 8; x >= 0; x--) {
        // create cell object
        let val = input[y][x];
        let isSet = val >= 1 && val <= 9;
        var cell = buildNewCell(val, isSet);

        // store cell in each state object
        let h = 3 * Math.floor(y / 3) + Math.floor(x / 3);
        let r = 3 * (y % 3) + (x % 3);

        sudoku[h][r] = cell;
        houses[h][r] = val;
        rows[y][x] = val;
        cols[x][y] = val;

        if (!isSet) {
          var s = new Set();
          for (let i = 1; i <= 9; i++) s.add(i);

          unsolved.push({
            row: y,
            col: x,
            house: h,
            room: r,
            suspects: s,
          });
        }
      }
    }

    // identify each cell's suspect values
    let axes = [rows, cols, houses];
    for (let i = 0; i < unsolved.length; i++) {
      let cell = unsolved[i];
      findSuspects(cell, axes);

      // update respective cells note value
      sudoku[cell.house][cell.room].notes = convert2Notes(cell.suspects);
    }

    // push to Sudoku as props
    localState = {
      sudoku: sudoku,
      houses: houses,
      rows: rows,
      cols: cols,
      unsolved: unsolved,
    };
    setState(localState);

    console.log("...Sudoku Loaded");
  };

  const convert2Notes = (suspects) => {
    var notes = [false, false, false, false, false, false, false, false, false];
    suspects.forEach((val) => {
      notes[val - 1] = true;
    });
    return notes;
  };

  const reportSolution = (name, target, suspect) => {
    // report to console
    console.log(
      name + "\t@ (" + target.row + "," + target.col + ") = " + suspect
    );

    // update state w suspect data
    state.sudoku[target.house][target.room].val = suspect;
    state.houses[target.house][target.room] = suspect;
    state.rows[target.row][target.col] = suspect;
    state.cols[target.col][target.row] = suspect;

    // update affected cell notes
    for (let i = 0; i < state.unsolved.length; i++) {
      let cell = state.unsolved[i];
      if (
        cell.row === target.row ||
        cell.col === target.col ||
        cell.house === target.house
      ) {
        findSuspects(state.unsolved[i]);

        state.sudoku[cell.house][cell.room].notes = convert2Notes(
          cell.suspects
        );
      }
    }

    // push state as props to Sudoku
    localState = {
      sudoku: state.sudoku,
      houses: state.houses,
      rows: state.rows,
      cols: state.cols,
      unsolved: state.unsolved,
    };
    setState(localState);
    return true;
  };

  // TODO: replace useEffect w this once tested
  const getNextSolution = () => {
    if (state.unsolved.length === 0) {
      console.log("Nothing left to solve.");
    } else {
      // Naked Single check

      // TEST nakedSingle
      for (let i = 0; i < state.unsolved.length && i < 81; i++) {
        if (nakedSingle.check(state.unsolved[i]) > 0) {
          // remove cell from unsolved list
          var target = state.unsolved.splice(i, 1)[0];
          let suspect = target.suspects.values().next().value;
          return reportSolution(nakedSingle.name, target, suspect);
        }
      }

      // TEST hiddenSingle
      for (let i = 0; i < state.unsolved.length && i < 81; i++) {
        if (hiddenSingle.check(state.unsolved[i], state) > 0) {
          // remove cell from unsolved list
          let target = state.unsolved.splice(i, 1)[0];
          let suspect = target.suspects.values().next().value;
          return reportSolution(hiddenSingle.name, target, suspect);
        }
      }

      console.log("No solution was found.");
      return false;
    }
  };

  // const isSameCell = (c1, c2) => {
  //   return c1.row === c2.row && c1.col === c2.col;
  // };

  const findSuspects = (
    cell,
    axes = [state.rows, state.cols, state.houses]
  ) => {
    let indexes = [cell.row, cell.col, cell.house];

    for (let i = 0; i < 3; i++) {
      // loops through rows, cols, houses this cell belongs to
      axes[i][indexes[i]].forEach((val) => {
        // if the value is set, remove it from suspect list
        if (val >= 1 && val <= 9) {
          cell.suspects.delete(val);
        }
      });
    }
  };

  const buildEmpty2DArray = () => {
    var result = new Array(9);
    for (let i = 0; i < 9; i++) {
      result[i] = new Array(9);
    }
    return result;
  };

  const buildNewCell = (v, s) => {
    return {
      val: v,
      set: s,
      notes: [false, false, false, false, false, false, false, false, false],
    };
  };

  return (
    <StyledDiv>
      <Sudoku houses={state.sudoku}></Sudoku>
      <Controls>
        <Button onClick={() => loadSudoku(input)}>load</Button>
        <Button onClick={getNextSolution}>solve</Button>
      </Controls>
    </StyledDiv>
  );
};

const StyledDiv = styled.div`
  display: grid;
  grid-template-rows: min(80vw, 80vh) 1fr;
  grid-template-columns: 1fr;
  grid-gap: 2rem;
  width: min(80vw, 80vh);
  height: 100%;
`;

const Controls = styled.div`
  display: flex;
  flex-direction: row;
  gap: 2em;
  width: 100%;
`;

const Button = styled.button`
  flex-grow: 1;
  font-size: 24px;

  border: 2px solid ${colors.sudokuBorder};
  color: ${colors.sudokuBorder};
  background-color: transparent;
  padding: 0.2em;
  border-radius: 1em;
  transition: all 0.3s ease;
  &:hover {
    background-color: ${colors.sudokuBorder};
    color: ${colors.appBG};
    transform: scale(1.05);
  }
  &:active {
    transition: all 0.05 ease-in;
    background-color: ${colors.sudokuBG1};
    transform: scale(1.025);
    color: ${colors.sudokuBorder};
  }
`;
