export const nakedSingle = {
  name: "Naked Single",
  check: (cell, state, showcase) => {
    // if this cell has only one suspect...
    if (cell.suspects.size === 1) {
      // ...report that suspect's value.

      // TODO: replace this...
      report.solution = {
        row: cell.row,
        col: cell.col,
        val: cell.suspects.values().next().value,
      };

      // ...with this
      // 1) create a copy of state.sudoku
      // 2) update this cell in the copy cell w new value
      // 3) return the new sudoku
      return showcase;
    }
    return report;
  },
};
