import { getDefaultNormalizer } from "@testing-library/react";
import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Sudoku } from "./Sudoku.js";
import { Cell } from "./Cell.js";
import { House } from "./House.js";
//import { colors } from "./../colors.js";

export const SudokuParent = (props) => {
  var stateHouses = [];

  var h0 = [6, 4, 9, 3, 5, 2, 1, 7, 8];
  var h1 = [0, 0, 0, 6, 1, 0, 0, 2, 9];
  var h2 = [1, 3, 2, 0, 7, 0, 5, 4, 6];
  var h3 = [8, 3, 1, 7, 6, 4, 9, 2, 5];
  var h4 = [9, 6, 7, 2, 5, 3, 8, 4, 1];
  var h5 = [2, 5, 4, 9, 8, 1, 3, 6, 7];
  var h6 = [2, 0, 0, 5, 0, 0, 0, 8, 7];
  var h7 = [5, 7, 6, 4, 9, 8, 1, 3, 2];
  var h8 = [4, 1, 8, 7, 2, 3, 6, 9, 5];
  var houses = [h0, h1, h2, h3, h4, h5, h6, h7, h8];

  var r0 = [6, 4, 9, 0, 0, 0, 1, 3, 2];
  var r1 = [3, 5, 2, 6, 1, 0, 0, 7, 0];
  var r2 = [1, 7, 8, 0, 2, 9, 5, 4, 6];
  var r3 = [8, 3, 1, 9, 6, 7, 2, 5, 4];
  var r4 = [7, 6, 4, 2, 5, 3, 9, 8, 1];
  var r5 = [9, 2, 5, 8, 4, 1, 3, 6, 7];
  var r6 = [2, 0, 0, 5, 7, 6, 4, 1, 8];
  var r7 = [5, 0, 0, 4, 9, 8, 7, 2, 3];
  var r8 = [0, 8, 7, 1, 3, 2, 6, 9, 5];
  var rows = [r0, r1, r2, r3, r4, r5, r6, r7, r8];

  var c0 = [6, 3, 1, 8, 7, 9, 2, 5, 0];
  var c1 = [4, 5, 7, 3, 6, 2, 0, 0, 8];
  var c2 = [9, 2, 8, 1, 4, 5, 0, 0, 7];
  var c3 = [0, 6, 0, 9, 2, 8, 5, 4, 1];
  var c4 = [0, 1, 2, 6, 5, 4, 7, 9, 3];
  var c5 = [0, 0, 9, 7, 3, 1, 6, 8, 2];
  var c6 = [1, 0, 5, 2, 9, 3, 4, 7, 6];
  var c7 = [3, 7, 4, 5, 8, 6, 1, 2, 9];
  var c8 = [2, 0, 6, 4, 1, 7, 8, 3, 5];
  var cols = [c0, c1, c2, c3, c4, c5, c6, c7, c8];

  var unsolved = [];
  var input = rows;

  const [solveState, setSolveState] = useState(houses);

  useEffect(() => {
    loadSudoku(input);
    console.log("NEXT: " + getNextSolution());
    console.log("NEXT: " + getNextSolution());
    console.log("NEXT: " + getNextSolution());
    console.log("NEXT: " + getNextSolution());
    console.log("NEXT: " + getNextSolution());
    console.log("NEXT: " + getNextSolution());
    console.log("NEXT: " + getNextSolution());
  }, []);

  const loadSudoku = (input) => {
    // input is a 2d array of all initial values
    stateHouses = buildEmpty2DArray();
    houses = buildEmpty2DArray();
    rows = buildEmpty2DArray();
    cols = buildEmpty2DArray();
    unsolved = [];

    for (var y = 0; y < 9; y++) {
      for (var x = 0; x < 9; x++) {
        // create cell object
        let val = input[y][x];
        let isSet = val >= 1 && val <= 9;

        var cell = buildNewCell(val, isSet);

        // store cell in each state object
        let h = 3 * Math.floor(y / 3) + Math.floor(x / 3);
        let r = 3 * (y % 3) + (x % 3);

        stateHouses[h][r] = cell;
        houses[h][r] = val;
        rows[y][x] = val;
        cols[x][y] = val;

        if (!isSet) {
          var s = new Set();
          for (var i = 1; i <= 9; i++) s.add(i);

          unsolved.push({
            row: y,
            col: x,
            house: h,
            room: r,
            suspects: s,
          });
        }
      }
    }

    // identify each cell's suspect values
    for (var i = 0; i < unsolved.length; i++) {
      findSuspects(unsolved[i]);
    }

    // TODO: push to Sudoku as props
    setSolveState(stateHouses);
  };

  // TODO: replace useEffect w this once tested
  const getNextSolution = () => {
    // Naked Single check
    for (var i = 0; i < unsolved.length; i++) {
      if (unsolved[i].suspects.size == 1) {
        // remove cell from unsolved list
        var cell = unsolved[i];
        console.log("SPLICE: " + unsolved.splice(i, 1));
        let suspect = cell.suspects.values().next().value;

        // report to console
        console.log(
          "Naked Single @ (" + cell.row + "," + cell.col + ") = " + suspect
        );

        // update state
        stateHouses[cell.house][cell.room].val = suspect;

        // TODO: push state as props to Sudoku
        setSolveState(stateHouses);
        return suspect;
      }
    }

    // Hidden Single check
    // ...
  };

  const findSuspects = (cell) => {
    let axes = [rows, cols, houses];
    let indexes = [cell.row, cell.col, cell.house];

    for (var i = 0; i < 3; i++) {
      console.log("axis: " + i + "\tindex: " + indexes[i]);
      axes[i][indexes[i]].forEach((val) => {
        if (val >= 1 && val <= 9) {
          cell.suspects.delete(val);
        }
      });
    }

    // // TODO: remove this if axes replacement works
    // rows[cell.row].forEach((val) => {
    //   if (val > 0 && val <= 9) {
    //     cell.suspects.delete(val);
    //   }
    // });
    // cols[cell.col].forEach((val) => {
    //   if (val > 0 && val <= 9) {
    //     cell.suspects.delete(val);
    //   }
    // });
    // houses[cell.house].forEach((val) => {
    //   if (val > 0 && val <= 9) {
    //     cell.suspects.delete(val);
    //   }
    // });
  };

  const buildEmpty2DArray = () => {
    var result = new Array(9);
    for (var i = 0; i < 9; i++) {
      result[i] = new Array(9);
    }
    return result;
  };

  const buildNewCell = (v, s) => {
    return {
      val: v,
      set: s,
      notes: [false, false, false, false, false, false, false, false, false],
    };
  };

  return (
    <StyledDiv>
      <Sudoku houses={solveState}></Sudoku>
    </StyledDiv>
  );
};

const StyledDiv = styled.div`
  display: grid;
  grid-template-rows: repeat(3, 1fr);
  grid-template-columns: repeat(3, 1fr);
  width: min(90vw, 90vh);
  height: min(90vw, 90vh);
`;
