import React, { useState } from "react";
import styled from "styled-components";
import { colors } from "../colors.js";
import { Sudoku } from "./sudoku";
import {
  nakedSingle,
  hiddenSingle,
  nakedPair,
  lockedCandidate,
  pointingTuple,
} from "./techniques";

export const SudokuController = (props) => {
  const [state, setState] = useState([]);
  var techniques = [
    nakedSingle,
    hiddenSingle,
    nakedPair,
    lockedCandidate,
    pointingTuple,
  ];

  // Naked Single Test
  // var input = [
  //   [0, 4, 9, 0, 0, 0, 0, 3, 0],
  //   [0, 5, 0, 6, 1, 0, 0, 0, 0],
  //   [0, 0, 8, 0, 2, 9, 5, 0, 6],
  //   [8, 0, 0, 9, 0, 7, 0, 0, 4],
  //   [7, 0, 0, 0, 0, 0, 0, 8, 1],
  //   [0, 2, 5, 0, 4, 1, 3, 0, 0],
  //   [2, 0, 0, 0, 7, 6, 0, 1, 0],
  //   [5, 0, 0, 4, 0, 8, 7, 0, 0],
  //   [0, 8, 7, 0, 0, 0, 0, 9, 5],
  // ];

  // Hidden Single Test
  var input = [
    [0, 7, 2, 3, 0, 0, 9, 0, 0],
    [0, 5, 0, 6, 0, 9, 0, 2, 0],
    [6, 0, 0, 0, 1, 0, 0, 3, 0],
    [0, 0, 0, 0, 0, 0, 2, 0, 0],
    [0, 4, 5, 8, 2, 3, 0, 7, 0],
    [0, 3, 0, 4, 0, 0, 0, 0, 8],
    [7, 0, 0, 0, 0, 0, 0, 0, 4],
    [0, 2, 0, 5, 0, 8, 0, 6, 0],
    [0, 0, 3, 0, 0, 1, 7, 0, 0],
  ];

  // input is a 2d array of starting values
  const loadSudoku = (input) => {
    console.log("Loading Sudoku...");

    let rows = buildEmpty2DArray();
    let cols = buildEmpty2DArray();
    let houses = buildEmpty2DArray();
    let unsolved = new Map();

    // for every sudoku cell...
    for (let h = 0; h < 9; h++) {
      for (let r = 0; r < 9; r++) {
        let x = 3 * Math.floor(h / 3) + Math.floor(r / 3);
        let y = 3 * (h % 3) + (r % 3);

        // create cell object
        let val = input[y][x];
        let isUnsolved = !(val >= 1 && val <= 9);
        var cell = buildNewCell(y, x, h, val, !isUnsolved);

        // store cell in each state object
        houses[h][r] = cell;
        rows[y][x] = cell;
        cols[x][y] = cell;
        if (isUnsolved) {
          var s = new Set();
          for (let i = 1; i <= 9; i++) s.add(i);
          unsolved.set([y, x], {
            row: y,
            col: x,
            house: h,
            room: r,
            suspects: s,
          });
        }
      }
    }

    // identify each cell's suspect values
    let axes = [rows, cols, houses];
    for (let cell of unsolved.values()) {
      findSuspects(cell, axes);
      houses[cell.house][cell.room].suspects = cell.suspects;
      houses[cell.house][cell.room].notes = convert2Notes(cell.suspects);
    }

    // push to Sudoku as props
    let localState = {
      sudoku: { rows: rows, cols: cols, houses: houses },
      unsolved: unsolved,
    };
    setState(localState);

    console.log("...Sudoku Loaded");
  };

  const getNextSolution = () => {
    if (state.unsolved.size === 0) {
      console.log("Nothing left to solve.");
      return false;
    } else {
      // for each solving technique...
      for (let t = 0; t < techniques.length; t++) {
        // ...for each unsolved cell...
        for (let [key, val] of state.unsolved) {
          // ...if this cell can be solved w this technique...
          let solution = techniques[t].check(val, state);
          if (solution > 0) {
            // ...report the solution.
            let cell = val;
            state.unsolved.delete(key);
            reportSolution(techniques[t], cell, solution);
            return true;
          }
        }
      }
      console.log("No solution was found.");
      return false;
    }
  };

  const reportSolution = (technique, cell, solution) => {
    // report to console
    console.log(
      technique.name + "\t@ (" + cell.row + "," + cell.col + ") = " + solution
    );

    // store solution in state
    cell.val = solution;
    cell.notes = convert2Notes(cell.suspects);
    state.sudoku.houses[cell.house][cell.room] = cell;
    state.sudoku.rows[cell.row][cell.col] = cell;
    state.sudoku.cols[cell.col][cell.row] = cell;

    // update affected cell notes
    for (let affected of state.unsolved.values()) {
      // TODO: this behavior must be able to manage technique-specific note updates
      if (
        affected.row === cell.row ||
        affected.col === cell.col ||
        affected.house === cell.house
      ) {
        findSuspects(affected);
        state.sudoku.houses[affected.house][
          affected.room
        ].notes = convert2Notes(affected.suspects);
      }
    }

    // push state as props to Sudoku
    let localState = {
      sudoku: state.sudoku,
      unsolved: state.unsolved,
    };
    setState(localState);
    return true;
  };

  const buildEmpty2DArray = () => {
    var result = new Array(9);
    for (let i = 0; i < 9; i++) {
      result[i] = new Array(9);
    }
    return result;
  };

  const buildNewCell = (r, c, h, v, s) => {
    return {
      row: r,
      col: c,
      house: h,
      val: v,
      set: s,
      notes: [false, false, false, false, false, false, false, false, false],
    };
  };

  const findSuspects = (
    cell,
    axes = [state.sudoku.rows, state.sudoku.cols, state.sudoku.houses]
  ) => {
    let indexes = [cell.row, cell.col, cell.house];
    // for each axis (row, col, house) this cell belongs to...
    for (let i = 0; i < 3; i++) {
      // ...for each cell in that axis...
      axes[i][indexes[i]].forEach((other) => {
        // ...if the cell's value is set...
        if (other.val >= 1 && other.val <= 9) {
          // ...remove it from suspect list.
          cell.suspects.delete(other.val);
        }
      });
    }
  };

  const convert2Notes = (suspects) => {
    var notes = [false, false, false, false, false, false, false, false, false];
    suspects.forEach((val) => {
      notes[val - 1] = true;
    });
    return notes;
  };

  return (
    <StyledDiv>
      <Sudoku sudoku={state.sudoku}></Sudoku>
      <Controls>
        <Button onClick={() => loadSudoku(input)}>load</Button>
        <Button onClick={getNextSolution}>solve</Button>
      </Controls>
    </StyledDiv>
  );
};

const StyledDiv = styled.div`
  display: grid;
  grid-template-rows: min(80vw, 80vh) 1fr;
  grid-template-columns: 1fr;
  grid-gap: 2rem;
  width: min(80vw, 80vh);
  height: 100%;
`;

const Controls = styled.div`
  display: flex;
  flex-direction: row;
  gap: 2em;
  width: 100%;
`;

const Button = styled.button`
  flex-grow: 1;
  font-size: 24px;

  border: 2px solid ${colors.sudokuBorder};
  color: ${colors.sudokuBorder};
  background-color: transparent;
  padding: 0.2em;
  border-radius: 1em;
  transition: all 0.3s ease;
  &:hover {
    background-color: ${colors.sudokuBorder};
    color: ${colors.appBG};
    transform: scale(1.05);
  }
  &:active {
    transition: all 0.05 ease-in;
    background-color: ${colors.sudokuBG1};
    transform: scale(1.025);
    color: ${colors.sudokuBorder};
  }
`;
