export const nakedSingle = {
  name: "Naked Single",
  check: (cell) => {
    let report = {
      oNotes: new Map(),
    };
    // if this cell has only one suspect...
    if (cell.suspects.size === 1) {
      // ...report that suspect's value.
      let val = cell.suspects.values().next().value;
      let notes = new Set();
      notes.add(val);
      report.oNotes.set([col.row, col.col], notes);
      return val;
    }
    return 0;
  },
};
