import React, { useState } from "react";
import styled from "styled-components";
import { Sudoku } from "./Sudoku.js";
import { colors } from "./../colors.js";

export const SudokuParent = (props) => {
  const [sudoku, setSudoku] = useState();
  const [rows, setRows] = useState([]);
  const [cols, setCols] = useState();
  const [houses, setHouses] = useState();
  const [unsolved, setUnsolved] = useState([]);

  var sudokuState = [];
  //var houses, rows, cols;
  var input = [
    [0, 4, 9, 0, 0, 0, 0, 3, 0],
    [0, 5, 0, 6, 1, 0, 0, 0, 0],
    [0, 0, 8, 0, 2, 9, 5, 0, 6],
    [8, 0, 0, 9, 0, 7, 0, 0, 4],
    [7, 0, 0, 0, 0, 0, 0, 8, 1],
    [0, 2, 5, 0, 4, 1, 3, 0, 0],
    [2, 0, 0, 0, 7, 6, 0, 1, 0],
    [5, 0, 0, 4, 0, 8, 7, 0, 0],
    [0, 8, 7, 0, 0, 0, 0, 9, 5],
  ];

  //useEffect(() => {
  const initSudoku = () => {
    loadSudoku(input);

    while (unsolved.length > 0 && getNextSolution());
  };
  //}, []);

  // input is a 2d array of all initial values
  const loadSudoku = (input) => {
    console.log("***SUDOKU LOADING***");

    function hardSet(state, props) {
      return { ...state, buildEmpty2DArray };
    }

    sudokuState = buildEmpty2DArray();
    setHouses(hardSet());
    setRows(hardSet);
    setCols(hardSet);
    setUnsolved([]);
    console.log(houses);

    for (var y = 0; y < 9; y++) {
      for (var x = 0; x < 9; x++) {
        // create cell object
        let val = input[y][x];
        let isSet = val >= 1 && val <= 9;
        var cell = buildNewCell(val, isSet);

        // store cell in each state object
        let h = 3 * Math.floor(y / 3) + Math.floor(x / 3);
        let r = 3 * (y % 3) + (x % 3);

        sudokuState[h][r] = cell;
        houses[h][r] = val;
        rows[y][x] = val;
        cols[x][y] = val;

        if (!isSet) {
          var s = new Set();
          for (let i = 1; i <= 9; i++) s.add(i);

          unsolved.push({
            row: y,
            col: x,
            house: h,
            room: r,
            suspects: s,
          });
        }
      }
    }

    //setAxes([rows, cols, houses]);
    // identify each cell's suspect values
    setRows(rows);
    console.log(rows);
    for (let i = 0; i < unsolved.length; i++) {
      findSuspects(unsolved[i]);
    }

    // push to Sudoku as props
    setUnsolved(unsolved);
    console.log(unsolved);
    setSudoku(sudokuState);
    console.log(sudoku);
    console.log("***SUDOKU LOADED***");
  };

  // TODO: replace useEffect w this once tested
  const getNextSolution = () => {
    // could update this more efficiently if previous solution tracking is added
    // identify each cell's suspect values
    console.log(unsolved);
    for (let i = 0; i < unsolved.length; i++) {
      findSuspects(unsolved[i]);
    }

    // Naked Single check
    for (var i = 0; i < unsolved.length && i < 81; i++) {
      console.log("looop " + i);
      if (unsolved[i].suspects.size === 1) {
        // remove cell from unsolved list
        var cell = unsolved[i];
        //console.log(unsolved);
        unsolved.splice(i, 1);
        let suspect = cell.suspects.values().next().value;

        // report to console
        console.log(
          "Naked Single @ (" + cell.row + "," + cell.col + ") = " + suspect
        );

        // update all state holders
        sudokuState[cell.house][cell.room].val = suspect;
        houses[cell.house][cell.room] = suspect;
        rows[cell.row][cell.col] = suspect;
        cols[cell.col][cell.row] = suspect;

        // push state as props to Sudoku
        setSudoku(sudokuState);
        return true;
      }
    }

    // Hidden Single check
    // ...

    return false;
  };

  const findSuspects = (cell) => {
    let axes = [rows, cols, houses];
    let indexes = [cell.row, cell.col, cell.house];

    for (var i = 0; i < 3; i++) {
      // loops through rows, cols, houses this cell belongs to
      console.log("axis: " + i + "\tindex: " + indexes[i]);
      console.log(axes[i][indexes[i]]);
      axes[i][indexes[i]].forEach((val) => {
        // if the value is set, remove it from suspect list
        if (val >= 1 && val <= 9) {
          cell.suspects.delete(val);
        }
      });
    }
  };

  const buildEmpty2DArray = () => {
    var result = new Array(9);
    for (var i = 0; i < 9; i++) {
      result[i] = new Array(9);
    }
    return result;
  };

  const buildNewCell = (v, s) => {
    return {
      val: v,
      set: s,
      notes: [false, false, false, false, false, false, false, false, false],
    };
  };

  return (
    <StyledDiv>
      <Sudoku houses={sudoku}></Sudoku>
      <Controls>
        <Button onClick={() => loadSudoku(input)}>load</Button>
        <Button onClick={getNextSolution}>solve</Button>
      </Controls>
    </StyledDiv>
  );
};

const StyledDiv = styled.div`
  display: grid;
  grid-template-rows: min(80vw, 80vh) 1fr;
  grid-template-columns: 1fr;
  grid-gap: 2rem;
  width: min(80vw, 80vh);
  height: 100%;
`;

const Controls = styled.div`
  display: flex;
  flex-direction: row;
  gap: 2em;
  width: 100%;
`;

const Button = styled.button`
  flex-grow: 1;
  font-size: 24px;

  border: 2px solid ${colors.sudokuBorder};
  color: ${colors.sudokuBorder};
  background-color: transparent;
  padding: 0.2em;
  border-radius: 1em;
  &:hover {
    background-color: ${colors.sudokuBorder};
    color: ${colors.appBG};
    transition: all 0.2s ease-in-out;
    transform: scale(1.05);
  }
`;
