//import React from "react";

// const NakedPair = (props) => {
//   return <h2>naked pair</h2>;
// };

export const nakedPair = {
  name: "Naked Pair",
  check: (cell, state) => {
    let report = {
      solution: 0,
      clues: [],
    };
    if (cell.suspects.size === 2) {
      // THIS IS JUST CODE TO ADD NOTES
      let notes = [];
      for (let i = 0; i < 9; i++) {
        let note = 0;
        if (cell.suspects.has(i)) note = 1;
        notes.push(note);
      }
      report.clues.push({
        row: cell.row,
        col: cell.col,
        notes: notes,
      });
    }
    console.log("nakedPair" + cell + state);
    return report;
  },
};
