import React from "react";
import styled from "styled-components";
import { colors, animation } from "./../../params.js";

//TODO: clean this up!
export const Note = (props) => {
  return (
    <StyledDiv className={props.tag === 3 ? "style3" : ""}>
      {props.tag === 0 && <Overlay>X</Overlay>}
      <span className={"style" + props.tag}>{props.num}</span>
    </StyledDiv>
  );
};

Note.defaultProps = {
  num: 0,
  tag: 0,
};

const Overlay = styled.div`
  z-index: 1;
  position: absolute;
  width: 100%;
  height: 100%;

  color: transparent;
  font-weight: normal;
  font-size: 125%;

  animation: crossOutOverlay ${animation.speed} ease-in-out;
  @keyframes crossOutOverlay {
    ${animation.midPoint} {
      color: ${colors.noteAccent};
      font-weight: ${animation.fontEmphasis};
    }
  }
`;

const StyledDiv = styled.div`
  position: relative;
  display: flex;
  flex-grow: 1;
  justify-content: center;
  align-items: center;

  height: 100%;
  width: 100%;
  color: ${colors.noteNormal};
  font-size: 55%;
  overflow: hidden;

  .style-1,
  .style-2 {
    color: transparent;
    animation: crossOutNum ${animation.speed} ease-in;

    @keyframes crossOutNum {
      0% {
        color: ${colors.noteNormal};
      }
      ${animation.midPoint} {
        color: ${colors.noteNormal};
        font-weight: ${animation.fontEmphasis};
      }
    }
  }

  .style0 {
    color: transparent;
    animation: fadeOut ${animation.speed} ease-in;

    @keyframes fadeOut {
      0% {
        color: ${colors.noteNormal};
      }
      ${animation.midPoint} {
        color: ${colors.noteNormal};
      }
    }
  }

  .style1 {
    font-weight: normal;
  }

  .style2,
  .style3 {
    color: ${colors.noteAccent};
    font-weight: bold;

    animation: pulseNum ${animation.speed} ease-in-out;

    @keyframes pulseNum {
      0% {
        color: ${colors.noteNormal};
      }
      ${animation.midPoint} {
        color: ${colors.noteAccent};
        font-size: ${animation.fontGrow};
      }
    }
  }
  &.style3 {
    animation: pulseOverlay ${animation.speed} ease-in-out;

    @keyframes pulseOverlay {
      ${animation.midPoint} {
        background-color: ${colors.sudokuFG};
      }
    }
  }
`;
