//import React from "react";

// const HiddenSingleComponent = (props) => {
//   return <h2>naked single</h2>;
// };

export const hiddenSingle = {
  name: "Hidden Single",
  check: (cell, state) => {
    let axes = [state.sudoku.rows, state.sudoku.cols, state.sudoku.houses];
    let indexes = [cell.row, cell.col, cell.house];
    for (let a = 0; a < 3; a++) {
      let axis = axes[a][indexes[a]];
      let unseen = Array.from(cell.suspects);
      for (let i = 0; i < 9; i++) {
        let other = axis[i];
        console.log(other);

        if (
          other.val === 0 &&
          !(cell.col === other.col && cell.row === other.row)
        ) {
          unseen = unseen.filter((u) => !other.suspects.has(u));
        }
      }
      if (unseen.length > 0) {
        return unseen[0];
      }
    }
    return 0;
  },
};
