import React from "react";
import styled from "styled-components";
import { colors } from "./../colors.js";

export const Note = (props) => {
  return (
    <StyledDiv>
      {props.show ? (
        <Hello>
          <span className={props.style}>{props.num}</span>
        </Hello>
      ) : props.animate ? (
        <ByeBye>{props.num}</ByeBye>
      ) : (
        <Gone>{props.num}</Gone>
      )}
    </StyledDiv>
  );
};

Note.defaultProps = {
  num: 0,
  tag: "open",
  show: true,
  animate: false,
};

const StyledDiv = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;

  height: 100%;
  width: 100%;
  overflow: hidden;
  color: ${colors.noteNormal};

  font-size: 1rem;

  .closed {
    text-decoration: line-through;
    color: ${colors.noteStrike};
  }
  .empty {
    visibility: hidden;
  }
  .open {
    font-weight: normal;
  }
  .accent {
    color: ${colors.noteAccent};
    font-weight: bold;
  }
`;

// TODO: refactor these animations, maybe disable on initial load

const ByeBye = styled.span`
  display: flex;
  justify-content: center;
  align-items: center;

  height: 100%;
  width: 100%;
  overflow: hidden;
  color: transparent;
  animation: fadeOut 1s ease-in-out;
  @keyframes fadeOut {
    0% {
      color: ${colors.noteNormal};
    }
    50% {
      //background-color: ${colors.sudokuBorder};
      color: transparent;
    }
  }
`;

const Gone = styled.span`
  display: flex;
  justify-content: center;
  align-items: center;

  height: 100%;
  width: 100%;
  overflow: hidden;
  color: transparent;
`;

const Hello = styled.span`
  display: flex;
  justify-content: center;
  align-items: center;

  height: 100%;
  width: 100%;
  overflow: hidden;
  /* animation: fadeOut 1s ease-in-out;
  @keyframes fadeOut {
    0% {
      color: ${colors.noteNormal};
    }
    50% {
      //background-color: ${colors.sudokuBorder};
      color: ${colors.sudokuFG};
    }
  } */
`;
