import React from "react";
import styled from "styled-components";
import { Note } from "./";
import { colors } from "./../../colors.js";

export const Cell = (props) => {
  return (
    <StyledDiv className={props.data.set ? "set" : ""}>
      <Value className={props.data.val > 0 ? "show" : "hide"}>
        <div>{props.data.val}</div>
      </Value>
      <Notes className={props.data.set ? "hide" : ""}>
        <Note num={1} show={props.data.notes[0]} />
        <Note num={2} show={props.data.notes[1]} />
        <Note num={3} show={props.data.notes[2]} />
        <Note num={4} show={props.data.notes[3]} />
        <Note num={5} show={props.data.notes[4]} />
        <Note num={6} show={props.data.notes[5]} />
        <Note num={7} show={props.data.notes[6]} />
        <Note num={8} show={props.data.notes[7]} />
        <Note num={9} show={props.data.notes[8]} />
      </Notes>
    </StyledDiv>
  );
};

Cell.defaultProps = {
  data: {
    val: -1,
    set: true,
    notes: [true, true, true, true, true, true, true, true, true],
  },
};

const StyledDiv = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;

  height: 100%;
  width: 100%;
  overflow: hidden;
  border: 1px solid ${colors.sudokuBorder};
  color: ${colors.sudokuFG};
  background-color: ${colors.sudokuBG1};
  box-sizing: border-box;
  position: relative;

  @keyframes pulse {
    0% {
      opacity: 0;
    }
    50% {
      background-color: ${colors.sudokuFG};
      color: ${colors.sudokuBG1};
      opacity: 1;
    }
  }

  &.set {
    background-color: ${colors.sudokuSet};
  }

  .show {
    background-color: inherit;
    animation: pulse 1s ease-in-out;
  }

  .hide {
    opacity: 0;
  }
`;

const Value = styled.div`
  height: 100%;
  width: 100%;

  display: flex;
  justify-content: center;
  align-items: center;

  z-index: 10;
`;

const Notes = styled.div`
  position: absolute;
  display: grid;
  grid-template-rows: repeat(3, 1fr);
  grid-template-columns: repeat(3, 1fr);
  height: 100%;
  width: 100%;
`;
