import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Sudoku } from "./Sudoku.js";
//import { colors } from "./../colors.js";

export const SudokuParent = (props) => {
  const [sudoku, setSudoku] = useState();

  var sudokuState = [];
  var houses, rows, cols, unsolved;
  var input = [
    [6, 4, 9, 0, 0, 0, 1, 3, 2],
    [3, 5, 2, 6, 1, 0, 0, 7, 0],
    [1, 7, 8, 0, 2, 9, 5, 4, 6],
    [8, 3, 1, 9, 6, 7, 2, 5, 4],
    [7, 6, 4, 2, 5, 3, 9, 8, 1],
    [9, 2, 5, 8, 4, 1, 3, 6, 7],
    [2, 0, 0, 5, 7, 6, 4, 1, 8],
    [5, 0, 0, 4, 9, 8, 7, 2, 3],
    [0, 8, 7, 1, 3, 2, 6, 9, 5],
  ];

  //useEffect(() => {
  const initSudoku = () => {
    console.log("***SUDOKU LOADING***");
    loadSudoku(input);
    console.log("***SUDOKU LOADED***");

    while (unsolved.length > 0) {
      getNextSolution();
    }
  };
  //}, []);

  // input is a 2d array of all initial values
  const loadSudoku = (input) => {
    sudokuState = buildEmpty2DArray();
    houses = buildEmpty2DArray();
    rows = buildEmpty2DArray();
    cols = buildEmpty2DArray();
    unsolved = [];

    for (var y = 0; y < 9; y++) {
      for (var x = 0; x < 9; x++) {
        // create cell object
        let val = input[y][x];
        let isSet = val >= 1 && val <= 9;
        var cell = buildNewCell(val, isSet);

        // store cell in each state object
        let h = 3 * Math.floor(y / 3) + Math.floor(x / 3);
        let r = 3 * (y % 3) + (x % 3);

        sudokuState[h][r] = cell;
        houses[h][r] = val;
        rows[y][x] = val;
        cols[x][y] = val;

        if (!isSet) {
          var s = new Set();
          for (var i = 1; i <= 9; i++) s.add(i);

          unsolved.push({
            row: y,
            col: x,
            house: h,
            room: r,
            suspects: s,
          });
        }
      }
    }

    // identify each cell's suspect values
    for (var i = 0; i < unsolved.length; i++) {
      findSuspects(unsolved[i]);
    }

    // push to Sudoku as props
    setSudoku(sudokuState);
  };

  // TODO: replace useEffect w this once tested
  const getNextSolution = () => {
    // Naked Single check
    for (var i = 0; i < unsolved.length; i++) {
      if (unsolved[i].suspects.size == 1) {
        // remove cell from unsolved list
        var cell = unsolved[i];
        unsolved.splice(i, 1);
        let suspect = cell.suspects.values().next().value;

        // report to console
        console.log(
          "Naked Single @ (" + cell.row + "," + cell.col + ") = " + suspect
        );

        // update state
        sudokuState[cell.house][cell.room].val = suspect;

        // TODO: push state as props to Sudoku
        setSudoku(sudokuState);
        return suspect;
      }
    }

    // Hidden Single check
    // ...
  };

  const findSuspects = (cell) => {
    let axes = [rows, cols, houses];
    let indexes = [cell.row, cell.col, cell.house];

    for (var i = 0; i < 3; i++) {
      // loops through rows, cols, houses this cell belongs to
      axes[i][indexes[i]].forEach((val) => {
        // if the value is set, remove it from suspect list
        if (val >= 1 && val <= 9) {
          cell.suspects.delete(val);
        }
      });
    }
  };

  const buildEmpty2DArray = () => {
    var result = new Array(9);
    for (var i = 0; i < 9; i++) {
      result[i] = new Array(9);
    }
    return result;
  };

  const buildNewCell = (v, s) => {
    return {
      val: v,
      set: s,
      notes: [false, false, false, false, false, false, false, false, false],
    };
  };

  return (
    <StyledDiv>
      <Sudoku houses={sudoku}></Sudoku>
      <Button onClick={initSudoku}></Button>
    </StyledDiv>
  );
};

const StyledDiv = styled.div`
  display: grid;
  grid-template-rows: repeat(3, 1fr);
  grid-template-columns: repeat(3, 1fr);
  width: min(90vw, 90vh);
  height: min(90vw, 90vh);
`;

const Button = styled.button``;
