import { getDefaultNormalizer } from "@testing-library/react";
import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Sudoku } from "./Sudoku.js";
import { Cell } from "./Cell.js";
import { House } from "./House.js";
//import { colors } from "./../colors.js";

export const SudokuParent = (props) => {
  var h0 = [6, 4, 9, 3, 5, 2, 1, 7, 8];
  var h1 = [0, 0, 0, 6, 1, 0, 0, 2, 9];
  var h2 = [1, 3, 2, 0, 7, 0, 5, 4, 6];
  var h3 = [8, 3, 1, 7, 6, 4, 9, 2, 5];
  var h4 = [9, 6, 7, 2, 5, 3, 8, 4, 1];
  var h5 = [2, 5, 4, 9, 8, 1, 3, 6, 7];
  var h6 = [2, 0, 0, 5, 0, 0, 0, 8, 7];
  var h7 = [5, 7, 6, 4, 9, 8, 1, 3, 2];
  var h8 = [4, 1, 8, 7, 2, 3, 6, 9, 5];
  var hs = [h0, h1, h2, h3, h4, h5, h6, h7, h8];

  var r0 = [6, 4, 9, 0, 0, 0, 1, 3, 2];
  var r1 = [3, 5, 2, 6, 1, 0, 0, 7, 0];
  var r2 = [1, 7, 8, 0, 2, 9, 5, 4, 6];
  var r3 = [8, 3, 1, 9, 6, 7, 2, 5, 4];
  var r4 = [7, 6, 4, 2, 5, 3, 9, 8, 1];
  var r5 = [9, 2, 5, 8, 4, 1, 3, 6, 7];
  var r6 = [2, 0, 0, 5, 7, 6, 4, 1, 8];
  var r7 = [5, 0, 0, 4, 9, 8, 7, 2, 3];
  var r8 = [0, 8, 7, 1, 3, 2, 6, 9, 5];
  var rs = [r0, r1, r2, r3, r4, r5, r6, r7, r8];

  var c0 = [6, 3, 1, 8, 7, 9, 2, 5, 0];
  var c1 = [4, 5, 7, 3, 6, 2, 0, 0, 8];
  var c2 = [9, 2, 8, 1, 4, 5, 0, 0, 7];
  var c3 = [0, 6, 0, 9, 2, 8, 5, 4, 1];
  var c4 = [0, 1, 2, 6, 5, 4, 7, 9, 3];
  var c5 = [0, 0, 9, 7, 3, 1, 6, 8, 2];
  var c6 = [1, 0, 5, 2, 9, 3, 4, 7, 6];
  var c7 = [3, 7, 4, 5, 8, 6, 1, 2, 9];
  var c8 = [2, 0, 6, 4, 1, 7, 8, 3, 5];
  var cs = [c0, c1, c2, c3, c4, c5, c6, c7, c8];
  const [sudokuRows, setRows] = useState();
  const [sudokuHouses, setHouses] = useState();
  const [sudokuColumns, setColumns] = useState();

  useEffect(() => {
    var data;
    // data = getData();

    //setRows(data.getRows());
    //setHouses(data.getHouses());
    //setColumns(data.getColumns());

    // note all empty cells
    var emptyCells = getEmptyCells();
    emptyCells.forEach((cell) => {
      cell.notes = getPossibleVals(cell);
      console.log(cell.notes);
    });

    // Naked Single check
    emptyCells.forEach((cell) => {
      let noteCount = 0;
      let i = 0;
      let solutionVal = 0;
      cell.notes.forEach((val) => {
        if (val) {
          noteCount++;
          solutionVal = i;
        }
        i++;
      });
      if (noteCount == 1) {
        console.log(
          "Naked Single @ (" +
            cell.row +
            "," +
            cell.col +
            ") = " +
            (solutionVal + 1)
        );
      }
    });
  }, []);

  const getEmptyCells = () => {
    var result = [];
    var currRow = 0;
    var currCol = 0;
    rs.forEach((row) => {
      row.forEach((cell) => {
        if (cell == 0) {
          let currHouse = getHouse(currRow, currCol);
          result.push({
            row: currRow,
            col: currCol,
            house: currHouse,
            notes: [],
          });
        }
        currCol++;
      });
      currRow++;
      currCol = 0;
    });
    return result;
  };

  const getPossibleVals = (cell) => {
    var possibleVals = [true, true, true, true, true, true, true, true, true];
    rs[cell.row].forEach((cell) => {
      if (cell > 0 && cell <= 9) {
        possibleVals[cell - 1] = false;
      }
    });
    cs[cell.col].forEach((cell) => {
      if (cell > 0 && cell <= 9) {
        possibleVals[cell - 1] = false;
      }
    });
    hs[cell.house].forEach((cell) => {
      if (cell > 0 && cell <= 9) {
        possibleVals[cell - 1] = false;
      }
    });
    return possibleVals;
  };

  const getHouse = (row, col) => {
    return 3 * Math.floor(row / 3) + Math.floor(col / 3);
  };

  const loadSudoku = () => {
    // set props values? Is this fxn needed?
  };

  const getNextSolution = () => {
    // apply next technique to sudoku
    // return new state if change was found
    // return null if not
  };

  // TODO: export solution data to set Sudoku props
  const exportSudoku = () => {};

  return (
    <StyledDiv>
      <Sudoku></Sudoku>
    </StyledDiv>
  );
};

const StyledDiv = styled.div`
  display: grid;
  grid-template-rows: repeat(3, 1fr);
  grid-template-columns: repeat(3, 1fr);
  width: min(90vw, 90vh);
  height: min(90vw, 90vh);
`;
