import { Technique } from "./Technique.js";

export class NakedSingle extends Technique {
  constructor() {}

  // IDEA: export named fxns instead of creating individual classes?
  check(cell, state) {
    // Naked Single check
    for (let i = 0; i < state.unsolved.length; i++) {
      if (state.unsolved[i].suspects.size === 1) {
        // remove cell from unsolved list
        var target = state.unsolved.splice(i, 1)[0];
        let suspect = target.suspects.values().next().value;

        return reportSolution("Naked Single", target, suspect);
      }
    }
    return false;
  }
}
