import React from "react";
import styled from "styled-components";
import { colors } from "./../../colors.js";

export const Note = (props) => {
  return (
    <StyledDiv>
      {props.show ? (
        <Hello>
          <span className={props.tag}>{props.num}</span>
        </Hello>
      ) : (
        <ByeBye>{props.num}</ByeBye>
      )}
    </StyledDiv>
  );
};

Note.defaultProps = {
  num: 0,
  tag: "open",
  show: true,
};

const StyledDiv = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;

  height: 100%;
  width: 100%;
  color: ${colors.noteNormal};
  font-size: 55%;
  overflow: hidden;

  div {
    display: flex;
    justify-content: center;
    align-items: center;
  }
`;

// TODO: refactor these animations, maybe disable on initial load

const ByeBye = styled.span`
  color: transparent;
  animation: fadeOut 1s ease-in-out;
  @keyframes fadeOut {
    0% {
      color: ${colors.noteNormal};
    }
    75% {
      color: transparent;
    }
  }
`;

const Hello = styled.span`
  .closed {
    text-decoration: line-through;
    color: ${colors.noteStrike};
  }
  .empty {
    visibility: hidden;
  }
  .open {
    font-weight: normal;
  }
  .accent {
    color: ${colors.noteAccent};
    font-weight: bold;
  }
`;
